import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { Checkbox, Button } from '@material-ui/core';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredientes: ["Batatas", "Ovos", "Leite", "Farinha", "Bifes", "AÃ§ucar", "Sal", "Abacate", "Arroz", "Alho Frances"]
    }

    this.onAddItem = this.onAddItem.bind(this);
  }

  render() {
    return (
      <div style={{ flexGrow: 1 }} >
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" style={{ flexGrow: 1 }}>
              Recipe Finder
            </Typography>
          </Toolbar>
        </AppBar>
        <p style={{ marginLeft: 14 }}> O que eu tenho</p>
        <div style={{
          position: "fixed",
          right: 0,
          left: 0,
          top: 100,
          bottom: 50,
          overflowY: 'auto'
        }}>
          {
            this.state.ingredientes.map(function (item, i) {
              console.log('test');
              return <p key={i}><Checkbox />{item}</p>
            })
          }
          <p><Checkbox />Batatas</p>
          <p><Checkbox />Ovos</p>
          <p><Checkbox />Leite</p>
          <p><Checkbox />Farinha</p>
          <p><Checkbox />Bifes</p>
          <p><Checkbox />AÃ§ucar</p>
          <p><Checkbox />Sal</p>
          <p><Checkbox />Abacate</p>
          <p><Checkbox />Arroz</p>
          <p><Checkbox />Alho Frances</p>
        </div>
        <Button
          variant="contained"
          color="primary"
          style={{
            position: 'fixed',
            bottom: 0,
            left: 0,
            width: "100%",
            height: 50
          }}
        >
          Procurar
        </Button>
      </div>
    );
  }
}
