import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { Button } from '@material-ui/core';
import IngrdientsList from './Screens/IngredientsList/IngrdientsList';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredientes: ["Batatas", "Ovos", "Leite", "Farinha", "Bifes", "Açucar", "Sal", "Abacate", "Arroz", "Alho Frances"],
      selected: []
    }
    this.addItem = this.addItem.bind(this);

  }

  addItem(e) {
    if (e.target.checked) {
      let tmpArr = [];
      tmpArr = this.state.selected;
      tmpArr.push(e.target.id);
      this.setState({
        selected: tmpArr,
      })
    } else {
      let tmpId = e.target.id;
      let arr = this.state.selected.filter(e => e !== tmpId);
      this.setState({
        selected: arr
      })
    }
  }

  render() {
    return (
      <div style={{ flexGrow: 1 }} >
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" style={{ flexGrow: 1 }}>
              Recipe Finder
            </Typography>
          </Toolbar>
        </AppBar>
        <p style={{ marginLeft: 14 }}> O que eu tenho</p>
        <div style={{
          position: "fixed",
          right: 0,
          left: 0,
          top: 100,
          bottom: 50,
          overflowY: 'auto'
        }}>
          <IngrdientsList
            addItem={this.addItem}
            ingredientes={this.state.ingredientes}
          />
        </div>
        <Button
          variant="contained"
          color="primary"
          style={{
            position: 'fixed',
            bottom: 0,
            left: 0,
            width: "100%",
            height: 50
          }}
        >
          Procurar
        </Button>
      </div>
    );
  }
}
