import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { Checkbox, Button } from '@material-ui/core';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredientes: ["Batatas", "Ovos", "Leite", "Farinha", "Bifes", "Açucar", "Sal", "Abacate", "Arroz", "Alho Frances"]
    }

  }

  render() {
    return (
      <div style={{ flexGrow: 1 }} >
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" style={{ flexGrow: 1 }}>
              Recipe Finder
            </Typography>
          </Toolbar>
        </AppBar>
        <p style={{ marginLeft: 14 }}> O que eu tenho</p>
        <div style={{
          position: "fixed",
          right: 0,
          left: 0,
          top: 100,
          bottom: 50,
          overflowY: 'auto'
        }}>
          {
            this.state.ingredientes.map(function (item, i) {
              return <p key={i}><Checkbox id={item} />{item}</p>
            })
          }
        </div>
        <Button
          variant="contained"
          color="primary"
          style={{
            position: 'fixed',
            bottom: 0,
            left: 0,
            width: "100%",
            height: 50
          }}
        >
          Procurar
        </Button>
      </div>
    );
  }
}
